mapper.select:
SELECT * FROM Clause,Catalog

  Clause.cateLogid=Catalog.catalogId



        and Clause.lawsId=#{clause.lawsId.lawsId}



      and Clause.clauseNumber = #{clause.clauseNumber}


      and Clause.clauseKeyword like concat ('%',#{clause.clauseKeyword},'%')

mapper.insert:
 insert into Clause (clauseId, catelogId, lawsId,
  clauseNumber, clauseName, clauseVersions,
  clauseTitle, clauseText, clauseKeyword
  )
values (#{clauseid,jdbcType=INTEGER}, #{cateLogid,jdbcType=INTEGER}, #{lawsid,jdbcType=INTEGER},
  #{clausenumber,jdbcType=INTEGER}, #{clausename,jdbcType=VARCHAR}, #{clauseversions,jdbcType=INTEGER},
  #{clausetitle,jdbcType=VARCHAR}, #{clausetext,jdbcType=VARCHAR}, #{clausekeyword,jdbcType=VARCHAR}
  )
